import '../../interfaces/external/IWNative.sol';
import '../../interfaces/marketplace/exchange/IGildiExchange.sol';
import '../../interfaces/marketplace/exchange/IGildiExchangeSwapAdapter.sol';
import './GildiExchangePaymentBaseCore.sol';

import {IERC20} from '@openzeppelin/contracts/token/ERC20/IERC20.sol';
import {AccessControlUpgradeable} from '@openzeppelin/contracts-upgradeable/access/AccessControlUpgradeable.sol';
import {ReentrancyGuardUpgradeable} from '@openzeppelin/contracts-upgradeable/utils/ReentrancyGuardUpgradeable.sol';
import {ContextUpgradeable} from '@openzeppelin/contracts-upgradeable/utils/ContextUpgradeable.sol';
import {Initializable} from '@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol';

/// @title GildiExchangePaymentBaseUpgradeable
/// @notice Abstract upgradeable base contract implementing the payment flow for the marketplace.
/// @dev Inherits from Initializable, ContextUpgradeable, OwnableUpgradeable, and ReentrancyGuardUpgradeable.
/// @custom:security-contact security@gildi.io
/// @author Gildi Company
abstract contract GildiExchangePaymentBaseUpgradeable is
    GildiExchangePaymentBaseCore,
    Initializable,
    ContextUpgradeable,
    AccessControlUpgradeable,
    ReentrancyGuardUpgradeable
{
    // keccak256(abi.encode(uint256(keccak256("gildi.storage.GildiExchangePaymentBase")) - 1)) & ~bytes32(uint256(0xff))
    bytes32 private constant GildiExchangePaymentBaseStorageLocation =
        0x29b67bb007a31b15dbc8250e7a602c4b79c971e992017fd3dae3b054f8ae4e00;

    /// @notice Initializes the upgradeable contract.
    /// @param _gildiExchange The address of the Gildi Exchange.
    /// @param _initialDefaultAdmin The address of the initial default admin.
    /// @param _initialContractAdmin The address of the initial contract admin.
    function __GildiExchangePaymentBase_init(
        address _gildiExchange,
        address _initialDefaultAdmin,
        address _initialContractAdmin
    ) internal onlyInitializing {
        __GildiExchangePaymentBase_init_unchained(_gildiExchange, _initialDefaultAdmin, _initialContractAdmin);
    }

    /// @notice Performs unchained initialization for the upgradeable contract.
    /// @param _gildiExchange The address of the Gildi Exchange.
    /// @param _initialDefaultAdmin The address of the initial default admin.
    /// @param _initialContractAdmin The address of the initial contract admin.
    function __GildiExchangePaymentBase_init_unchained(
        address _gildiExchange,
        address _initialDefaultAdmin,
        address _initialContractAdmin
    ) internal onlyInitializing {
        __ReentrancyGuard_init();
        __AccessControl_init();
        GildiExchangePaymentBaseStorage storage s = _getGildiExchangePaymentBaseStorage();
        s.gildiExchange = IGildiExchange(_gildiExchange);

        _grantRole(DEFAULT_ADMIN_ROLE, _initialDefaultAdmin);
        _grantRole(ADMIN_ROLE, _initialContractAdmin);
    }

    //----- Admin Functions -----

    /// @notice Sets whether native payments are allowed for purchases.
    /// @param _allow True if native payments are enabled.
    function setPurchaseAllowNative(bool _allow) public onlyRole(ADMIN_ROLE) {
        super._setPurchaseAllowNative(_allow);
    }

    /// @notice Sets the allowed purchase status for a given source token.
    /// @param _token The address of the token.
    /// @param _allowed True to allow the token.
    function setAllowedPurchaseToken(address _token, bool _allowed) external onlyRole(ADMIN_ROLE) {
        super._setAllowedPurchaseToken(_token, _allowed);
    }

    /// @notice Adds a new aggregator/DEX adapter.
    /// @param _adapter The adapter to add.
    function addAdapter(IGildiExchangeSwapAdapter _adapter) external onlyRole(ADMIN_ROLE) {
        super._addAdapter(_adapter);
    }

    /// @notice Removes an adapter by instance.
    /// @param adapter The adapter instance to remove.
    function removeAdapter(IGildiExchangeSwapAdapter adapter) external onlyRole(ADMIN_ROLE) {
        super._removeAdapter(adapter);
    }

    /// @notice Sets the wrapped native token address.
    /// @param _wnative The address of the wrapped native token.
    function setWrappedNative(address _wnative) external onlyRole(ADMIN_ROLE) {
        super._setWrappedNative(_wnative);
    }

    /// @notice Removes an adapter by its index.
    /// @param index The index of the adapter to remove.
    function removeAdapter(uint256 index) external onlyRole(ADMIN_ROLE) {
        super._removeAdapter(index);
    }

    //----- Internal Storage Getter -----

    /// @dev Returns the storage pointer for unstructured storage.
    function _getStorage() internal pure override returns (GildiExchangePaymentBaseStorage storage) {
        return _getGildiExchangePaymentBaseStorage();
    }

    function _msgSender()
        internal
        view
        override(GildiExchangePaymentBaseCore, ContextUpgradeable)
        returns (address sender)
    {
        return super._msgSender();
    }

    /// @dev Internal function to retrieve the storage struct from a fixed slot.
    /// @return $ The storage pointer.
    function _getGildiExchangePaymentBaseStorage() private pure returns (GildiExchangePaymentBaseStorage storage $) {
        assembly {
            $.slot := GildiExchangePaymentBaseStorageLocation
        }
    }
}

