        uint256 tokenId;
        /// @notice The balance of the token.
        uint256 balance;
    }

    struct MintBatch {
        address to;
        uint256[] ids;
        uint256[] amounts;
        bytes data;
    }

    /// @notice Pause the contract.
    function pause() external;

    /// @notice Unpause the contract.
    function unpause() external;

    /// @notice The name of the token.
